import re

for _ in range(int(input())):
    u = ''.join(sorted(input()))
    try:
        assert re.search(r'[A-Z]{2}', u) # at least 2 uppercase English alphabet characters 
        assert re.search(r'\d\d\d', u) # at least 3 digits (0-9)
        assert not re.search(r'[^a-zA-Z0-9]', u) # only contain alphanumeric characters (a-z, A-Z & 0-9) 
        assert not re.search(r'(.)\1', u) # no character should repeat
        assert len(u) == 10 # must be exactly 10 characters
    except:
        print('Invalid')
    else:
        print('Valid')